mapper.select:
 	SELECT e.emp_no, bb.b_idx, bb.b_name, e.name, e.position, e.join_year, DATEDIFF(CURRENT_TIMESTAMP(), e.join_year) AS work_year, a.annualAdd, b.annualSub
	FROM emp e natural join branch bb
	LEFT JOIN (
	  SELECT emp_no, SUM(annual_time) AS annualAdd
	  FROM annual
	  WHERE YEAR(annual_date) = YEAR(CURDATE()) and annual_modifier = 1
	  GROUP BY emp_no
	) a ON e.emp_no = a.emp_no
	LEFT JOIN (
	  SELECT emp_no, SUM(annual_time) AS annualSub
	  FROM annual
	  WHERE YEAR(annual_date) = YEAR(CURDATE()) and annual_modifier = 2
	  GROUP BY emp_no
	) b ON e.emp_no = b.emp_no

		where 365 > DATEDIFF(CURRENT_TIMESTAMP(), e.join_year) and b_idx = #{param5}


		where DATEDIFF(CURRENT_TIMESTAMP(), e.join_year) >= 365 and b_idx = #{param5}


		where b_idx = #{param5}

	order by e.emp_no ${param4} limit #{param2} offset #{param3}

SELECT count(*) FROM emp e natural join branch b LEFT JOIN ( SELECT emp_no, SUM(annual_time) AS annualAdd FROM annual WHERE YEAR(annual_date) = YEAR(CURDATE()) and annual_modifier = 1 GROUP BY emp_no ) a ON e.emp_no = a.emp_no LEFT JOIN ( SELECT emp_no, SUM(annual_time) AS annualSub FROM annual WHERE YEAR(annual_date) = YEAR(CURDATE()) and annual_modifier = 2 GROUP BY emp_no ) b ON e.emp_no = b.emp_no
where 365 > DATEDIFF(CURRENT_TIMESTAMP(), e.join_year) and b_idx = #{param2}
where DATEDIFF(CURRENT_TIMESTAMP(), e.join_year) >= 365 and b_idx = #{param2}
where b_idx = #{param2}
order by e.emp_no desc

select sum(case when annual_modifier = 1 then annual_time end) as annual_add, 
	sum(case when annual_modifier = 2 then annual_time end) as annual_sub
from annual where emp_no = #{param1} and annual_kind = #{param2}
