mapper.update:
		    UPDATE pt_mem
 SET count = count - 1
 WHERE mem_no = #{mem_no}

UPDATE pt_mem pm
JOIN daily_pt dp ON pm.mem_no = dp.mem_no
SET pm.weight = #{af_weight}
WHERE pm.mem_no = #{mem_no}

   		UPDATE daily_pt SET 
   			dailypt_no = #{dailypt_no}
   			,emp_no = ${emp_no}
   			,mem_no = ${mem_no}
   			,aerobic = #{aerobic}
			,diet = #{diet}
			,pt_date = #{pt_date}
			,af_weight = #{af_weight}
			,str = #{str}

		WHERE dailypt_no = #{dailypt_no};


   	UPDATE pt_weight SET 

   			dailypt_no = #{param1}
   			,pt_name = #{param2}
			,pt_kg = #{param3}
			,pt_set = #{param4}

		WHERE dailypt_no = #{dailypt_no} AND weight_no = #{param5};

mapper.select:
select dp.dailypt_no, dp.mem_no, dp.pt_date, dp.emp_no, m.name AS member_name, e.name AS emp_name FROM daily_pt dp JOIN member m ON dp.mem_no = m.mem_no JOIN emp e ON dp.emp_no = e.emp_no
where m.mem_no LIKE #{searchInput}
or m.name LIKE #{searchInput}
